-- CREATE TABLE adhkar_after_prayer (
--     id INT AUTO_INCREMENT PRIMARY KEY,
--     category VARCHAR(255),
--     count VARCHAR(10),
--     description VARCHAR(255),
--     reference VARCHAR(255),
--     content TEXT
-- );

-- INSERT INTO adhkar_after_prayer (category, count, description, reference, content) VALUES
-- ('أذكار بعد السلام من الصلاة المفروضة', '01', '', '', 'أَسْـتَغْفِرُ الله، أَسْـتَغْفِرُ الله، أَسْـتَغْفِرُ الله.اللّهُـمَّ أَنْـتَ السَّلامُ ، وَمِـنْكَ السَّلام ، تَبارَكْتَ يا ذا الجَـلالِ وَالإِكْـرام .'),
-- ('أذكار بعد السلام من الصلاة المفروضة', '01', '', '', 'لا إلهَ إلاّ اللّهُ وحدَهُ لا شريكَ لهُ، لهُ المُـلْكُ ولهُ الحَمْد، وهوَ على كلّ شَيءٍ قَدير، اللّهُـمَّ لا مانِعَ لِما أَعْطَـيْت، وَلا مُعْطِـيَ لِما مَنَـعْت، وَلا يَنْفَـعُ ذا الجَـدِّ مِنْـكَ الجَـد.'),
-- ('أذكار بعد السلام من الصلاة المفروضة', '01', '', '', 'لا إلهَ إلاّ اللّه, وحدَهُ لا شريكَ لهُ، لهُ الملكُ ولهُ الحَمْد، وهوَ على كلّ شيءٍ قدير، لا حَـوْلَ وَلا قـوَّةَ إِلاّ بِاللهِ، لا إلهَ إلاّ اللّـه، وَلا نَعْـبُـدُ إِلاّ إيّـاه, لَهُ النِّعْـمَةُ وَلَهُ الفَضْل وَلَهُ الثَّـناءُ الحَـسَن، لا إلهَ إلاّ اللّهُ مخْلِصـينَ لَـهُ الدِّينَ وَلَوْ كَـرِهَ الكـافِرون.'),
-- ('أذكار بعد السلام من الصلاة المفروضة', '33', '', '', 'سُـبْحانَ اللهِ، والحَمْـدُ لله ، واللهُ أكْـبَر.'),
-- ('أذكار بعد السلام من الصلاة المفروضة', '01', '', '', 'لا إلهَ إلاّ اللّهُ وَحْـدَهُ لا شريكَ لهُ، لهُ الملكُ ولهُ الحَمْد، وهُوَ على كُلّ شَيءٍ قَـدير.'),
-- ('أذكار بعد السلام من الصلاة المفروضة', '10', 'عَشْر مَرّات بَعْدَ المَغْرِب وَالصّـبْح.', '', 'لا إلهَ إلاّ اللّهُ وحْـدَهُ لا شريكَ لهُ، لهُ المُلكُ ولهُ الحَمْد، يُحيـي وَيُمـيتُ وهُوَ على كُلّ شيءٍ قدير.'),
-- ('أذكار بعد السلام من الصلاة المفروضة', '01', 'بَعْد السّلامِ من صَلاةِ الفَجْر.', '', 'اللّهُـمَّ إِنِّـي أَسْأَلُـكَ عِلْمـاً نافِعـاً وَرِزْقـاً طَيِّـباً ، وَعَمَـلاً مُتَقَـبَّلاً.'),
-- ('أذكار بعد السلام من الصلاة المفروضة', '07', 'بعد صلاة الصبح والمغرب.', '', 'اللَّهُمَّ أَجِرْنِي مِنْ النَّار.'),
-- ('أذكار بعد السلام من الصلاة المفروضة', '01', '', '', 'اللَّهُمَّ أَعِنِّي عَلَى ذِكْرِكَ وَشُكْرِكَ وَحُسْنِ عِبَادَتِكَ.');

